CREATE database hospitalmanagement;
CREATE table users(username varchar(255),emailid varchar(255),password varchar(255),role varchar(255),name varchar(255),status varchar(255),PhoneNumber varchar(255) ,  secondaryPhone varchar(255),  secondaryEmail varchar(255),  mailingAddress varchar(255),  ecFirstName varchar(255),  ecLastName varchar(255),  ecEmailAddress varchar(255),  ecPhoneNumber varchar(255),  Age varchar(255),  DateOfBirth varchar(255),  affiliation varchar(255),  availableDays varchar(255),  availableHours varchar(255),  certification varchar(255),  Degree varchar(255),  department varchar(255),  experience varchar(255),  specialization varchar(255), insuranceEndDate varchar(255) , insuranceStartDate varchar(255) , insuranceProvider varchar(255) , insuranceID varchar(255) , medicalHistory varchar(255) , prescriptions varchar(255));
CREATE table appointments (appDay varchar(255),startTime varchar(255),endTime varchar(255),appDate varchar(255),attendingDoc varchar(255),DoctorName varchar(255),attendingNurse varchar(255),NurseName varchar(255),patient varchar(255),patientName varchar(255),idAppointments varchar(255),DoctorsNotes varchar(255),Cost varchar(255));